import React from "react"

import EditMovement from "./EditMovement"

const ListMovements = () => {
    const [list, setList] = React.useState([])
    const [popUp, setPopUp] = React.useState(false)

    const togglePopUp = () => {
        setPopUp(!popUp)
    }

    const getMovements = async () => {
        try {
            const response = await fetch("http://localhost:5000/movements")
            const jsonData = await response.json()
            setList(jsonData.slice(0,10))
            console.log(jsonData)
        } catch (e) {
            console.error(e.message)
        }
    }

    const deleteMovement = async (id) => {
        try {
            const deleteMovement = await fetch(`http://localhost:5000/movements/${id}`, {
                method: "DELETE"
            })
            setList(list.filter(list => list.movement_id !== id))
        } catch (e) {
            console.error(e.message)
        }
    }

    // Makes a fetch request tou our REST api everytime this is component is rendered
    React.useEffect(() => {
        getMovements()
    }, [])

    return (
        <React.Fragment>
            {" "}
            <table>
                <thead>
                <tr>
                    <th>Amount</th>
                    <th>Concept</th>
                    <th>Date</th>
                    <th>Type</th>
                    <th>Edit</th>
                    <th>Delete</th>
                </tr>
                </thead>
                <tbody>
                {/*<tr>
                    <td>John</td>
                    <td>Doe</td>
                    <td>john@example.com</td>
                </tr> */}
                {list.map(list => (
                    <tr key={list.movement_id}>
                        <td>{list.amount}</td>
                        <td>{list.concept}</td>
                        {/* <td>{list.dateM}</td>
                        <td>{list.typeM}</td> */}
                        <td>
                            {/* <EditMovement /> */}
                            {/* <div>
                                <input
                                type="button"
                                value="Click to Open Popup"
                                onClick={togglePopUp}
                                />
                                {popUp && <EditMovement
                                    movement={list}
                                    handleClose={togglePopUp}
                                />}
                            </div> */}
                        </td>
                        <td>
                            <button
                            onClick={() => deleteMovement(list.movement_id)}
                            >
                            Delete
                            </button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </React.Fragment>
    )
}

export default ListMovements
