import React from "react"
import { Modal, Button, Form, InputGroup, FormControl } from "react-bootstrap"

function EditMovement() {
    const [show, setShow] = React.useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
      <>
        <Button variant="primary" onClick={handleShow}>
          Edit
        </Button>

        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Edit movement</Modal.Title>
          </Modal.Header>
          <Modal.Body>
              <Form>
                <Form.Label>Amount</Form.Label>
                <InputGroup className="mb-3">
                    <InputGroup.Text>$</InputGroup.Text>
                    <FormControl 
                        value={amount} 
                        onChange={e => setAmount(e.target.value)} 
                        aria-label="Amount (to the nearest dollar)" 
                    />
                </InputGroup>
                <Form.Label>Concept</Form.Label>
                <InputGroup className="mb-3">
                    <FormControl
                        aria-describedby="basic-addon1"
                    />
                </InputGroup>
                <Form.Label>Type of Operation</Form.Label>
                {/* <Form.Select aria-label="Default select example">
                    <option>Select the type of operation...</option>
                    <option value="I">Income</option>
                    <option value="O">Outcome</option>
                </Form.Select> */}
                {['radio'].map((type) => (
                    <div key={`inline-${type}`} className="mb-3">
                    <Form.Check
                        inline
                        label="Income"
                        name="group1"
                        type={type}
                        id={`inline-${type}-1`}
                    />
                    <Form.Check
                        inline
                        label="Outcome"
                        name="group1"
                        type={type}
                        id={`inline-${type}-2`}
                    />
                    </div>
                ))}
                <Form.Label>Date</Form.Label>
                <FormControl className="mb-3" type="date"></FormControl>
                <Button className="mb-3" variant="primary" type="submit">
                    Submit
                </Button>
              </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={handleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }




