import React from "react"

const EditMovement = props => {
    const onSubmitForm = async e => {
        // Handle the form submitted

        // Prevent from refresh
        e.preventDefault()
        try {
          const body = { amount, concept, typeM, dateM }
          const response = await fetch(`http://localhost:5000/movements/${movement.movement_id}`, {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify(body)
          })
        //  Once the response has been sent, itś going to refresh and show the changes
          window.location = "/" 
        } catch (err) {
            console.error(err.message)
        }
    }

    return (
        <div className="popup-box">
            <div className="box">
                <span className="close-icon" onClick={props.handleClose}>x</span>
                {/* {props.content} */}
                <form onSubmit={onSubmitForm}>
                    <label>
                        Amount:
                        <input type="number" value={props.movement.amount} onChange={e => setAmount(e.target.value)} />
                    </label>
                    <label>
                        Concept:
                        <input type="text" value={props.movement.concept} onChange={e => setConcept(e.target.value)} />
                    </label>
                    <label>
                        Type of movement:
                        <select value={props.movement.typeM} disabled >
                            <option value="I">Income</option>
                            <option value="O">Outcome</option>
                        </select>
                    </label>
                    <label>
                        Date:
                        <input type="date" value={props.movement.dateM} onChange={e => setDateM(e.target.value)} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        </div>
    )
}

export default EditMovement
